package no.uib.info233.heap;/** * A class that implements the ADT priority queue by using a maxheap. *  * @author Frank M. Carrano * @author Timothy M. Henry * @version 4.0 */public final class HeapPriorityQueue<T extends Comparable<? super T>> implements PriorityQueueInterface<T> {    private MaxHeap<T> pq;    public HeapPriorityQueue() {        pq = new ArrayMaxHeap<>();    }    @Override    public void add(T entry) {        pq.add(entry);    }    @Override    public T remove() {        return pq.removeMax();    }    @Override    public T peek() {        return pq.getMax();    }    @Override    public boolean isEmpty() {        return pq.isEmpty();    }    @Override    public int getSize() {        return pq.getSize();    }    @Override    public void clear() {        pq.clear();    }}