package no.uib.info233.heap;/** * An interface for the ADT maxheap. * * @author Frank M. Carrano * @author Timothy M. Henry * @author age-admin * @version 4.0.uib */public interface MaxHeap<T extends Comparable<? super T>> {    /**     * Adds a new entry to this heap.     *     * @param entry An object to be added.     */    void add(T entry);    /**     * Removes and returns the largest item in this heap.     *     * @return Either the largest object in the heap or, if the heap is empty before     *         the operation, null.     */    T removeMax();    /**     * Retrieves the largest item in this heap.     *     * @return Either the largest object in the heap or, if the heap is empty, null.     */    T getMax();    /**     * Detects whether this heap is empty.     *     * @return True if the heap is empty, or false otherwise.     */    Boolean isEmpty();    /**     * Gets the size of this heap.     *     * @return The number of entries currently in the heap.     */    Integer getSize();    /**     * Removes all entries from this heap.     */    void clear();}