package no.uib.info233.tree;/** * An interface for the ADT binary tree. *  * @author Frank M. Carrano * @author Timothy M. Henry * @author Atle Geitung * @version 4.0.uib */public interface BinaryTree<T> extends Tree<T>, TreeIterator<T> {    /**     * Sets this binary tree to a new one-node binary tree.     *      * @param rootData The object that is the data for the new tree's root.     */    void setTree(T rootData);    /**     * Sets this binary tree to a new binary tree.     *      * @param rootData  The object that is the data for the new tree's root.     * @param leftTree  The left subtree of the new tree.     * @param rightTree The right subtree of the new tree.     */    void setTree(T rootData, BinaryTree<T> leftTree, BinaryTree<T> rightTree);}