package no.uib.info233.tree;/** * An interface of basic methods for the ADT tree. *  * @author Frank M. Carrano * @author Timothy M. Henry * @author Atle Geitung * @version 4.0.uib */public interface Tree<T> {    /**     * Get data from root node in the tree.     *      * @return data in root node     */    T getRootData();        /**     * Get height of tree.     *      * @return height     */    Integer getHeight();    /**     * Get number of nodes in the tree.     *      * @return number of nodes     */    Integer getNumberOfNodes();    /**     * Returns true if tree is empty (no nodes).     *      * @return true if tree is empty     */    Boolean isEmpty();    /**     * Clears the tree.     */    void clear();}